<?php/* * Following code will list all the products */// array for JSON response$response = array();// include db connect classrequire_once('db_connect.php');// connecting to db$db = new DB_CONNECT();$result = mysql_query("SELECT admin.maadmin ,admin.tenadmin,khachhang.makhachhang, khachhang.tenkhachhang,danhgia.madanhgia,danhgia.makhachhang,danhgia.tieudesanpham,danhgia.sosaodanhgia,danhgia.noidungbinhluan,danhgia.ngaydanhgia,trangthaidanhgia.madanhgia,trangthaidanhgia.trangthai, trangthaidanhgia.maadmin FROM trangthaidanhgia,danhgia,khachhang,admin where  trangthaidanhgia.maadmin = admin.maadmin and trangthaidanhgia.madanhgia = danhgia.madanhgia  and danhgia.makhachhang = khachhang.makhachhang and trangthaidanhgia.trangthai like '1' ") or die(mysql_error());// check for empty resultif (mysql_num_rows($result) > 0) {    $response["trangthaidanhgia"] = array();        while ($row = mysql_fetch_array($result)) {        // temp user array        $tungdanhgia = array();        $tungdanhgia["tenkhachhang"] = $row["tenkhachhang"];        $tungdanhgia["tenadmin"] = $row["tenadmin"];$tungdanhgia["tenkhachhang"] = $row["tenkhachhang"];$tungdanhgia["tieudesanpham"] = $row["tieudesanpham"];$tungdanhgia["sosaodanhgia"] = $row["sosaodanhgia"];$tungdanhgia["noidungbinhluan"] = $row["noidungbinhluan"];$tungdanhgia["ngaydanhgia"] = $row["ngaydanhgia"];        array_push($response["trangthaidanhgia"], $tungdanhgia);    }    // success    $response["success"] = 1;    // echoing JSON response    echo json_encode($response);} else {    // no products found    $response["success"] = 0;    $response["message"] = "No trangthai  found";    // echo no users JSON    echo json_encode($response);}?>