<?php/* * Following code will list all the products */// array for JSON response$response = array();// include db connect classrequire_once('db_connect.php');// connecting to db$db = new DB_CONNECT();$result = mysql_query("SELECT khachhang.makhachhang , trangthaitruycapuser.makhachhang, khachhang.tenkhachhang,trangthaitruycapuser.tentrangthaitruycapuser FROM khachhang,trangthaitruycapuser where   trangthaitruycapuser.makhachhang = khachhang.makhachhang  and tentrangthaitruycapuser like 'đang hoạt động' ") or die(mysql_error());// check for empty resultif (mysql_num_rows($result) > 0) {    $response["trangthaitruycap"] = array();        while ($row = mysql_fetch_array($result)) {        // temp user array        $tungdanhgia = array();        $tungdanhgia["tenkhachhang"] = $row["tenkhachhang"];$tungdanhgia["tentrangthaitruycapuser"] = $row["tentrangthaitruycapuser"];        array_push($response["trangthaitruycap"], $tungdanhgia);    }    // success    $response["success"] = 1;    // echoing JSON response    echo json_encode($response);} else {    // no products found    $response["success"] = 0;    $response["message"] = "No trangthai  found";    // echo no users JSON    echo json_encode($response);}?>